sys_script_client.script:
function onSubmit() {
	// check if split equally is false
	alert(g_form.getValue('split_equally'));
	if (g_form.getValue('split_equally') == 'false') {
		alert(g_form.getValue('total') + " total");
		var total = g_form.getValue('total');
		if (total <= 0) {
			alert('Total Expense Amount Should be Greaterthan 0!!');
			return false;
		}
		var individualAmountSum = 0;
		alert('user_amount: ' + JSON.stringify(g_form.getValue('user_amount')));
		var userAmounts = g_form.getValue('user_amount');
		// Iterate over each property and print name and value
		for(var userName in userAmounts) {
			individualAmountSum += userAmounts[userName];
		}

		if (total != individualAmountSum) {
			alert('Individual Expense Amount Should Match with the Total amount !!');
			return false;
		}
	}

}
